import{_ as r,C as o,c as n,o as s,j as e,G as i,ak as d,a as l}from"./chunks/framework.ME6v4i3z.js";const u=JSON.parse('{"title":"2025新年特辑","description":"MCJPG 2025新年特辑","frontmatter":{"layout":"doc","title":"2025新年特辑","description":"MCJPG 2025新年特辑"},"headers":[],"relativePath":"press/特别栏目/2025新年特辑/index.md","filePath":"press/特别栏目/2025新年特辑/index.md","lastUpdated":1761139711000}'),h={name:"press/特别栏目/2025新年特辑/index.md"};function c(p,a,_,b,m,P){const t=o("ArticleMetadata");return s(),n("div",null,[a[0]||(a[0]=e("h1",{id:"_2025新年特辑",tabindex:"-1"},[l("2025新年特辑 "),e("a",{class:"header-anchor",href:"#_2025新年特辑","aria-label":'Permalink to "2025新年特辑"'},"​")],-1)),i(t),a[1]||(a[1]=d('<p><strong>各位新年好</strong>，不知不觉，<strong>MCJPG</strong> 已经陪伴大家走过了半个年头<br><strong>MCJPG</strong> 向大家拜年啦</p><h2 id="年终总结" tabindex="-1">年终总结 <a class="header-anchor" href="#年终总结" aria-label="Permalink to &quot;年终总结&quot;">​</a></h2><p>在过去的一年里，<strong>MCJPG</strong> 的各项工作逐渐完善，没完成的烂尾项目也全部收尾<br> 这个年轻且充满活力的组织，终于迎来了规范化<br> 在寒假期间，我们对现有各成员服就行了<strong>复审</strong>，提升了玩家们的游戏体验<br> 对组织内部架构进行了改革，确保了决策的正确性</p><h2 id="年度项目-ai-chat" tabindex="-1">年度项目-AI Chat <a class="header-anchor" href="#年度项目-ai-chat" aria-label="Permalink to &quot;年度项目-AI Chat&quot;">​</a></h2><p>经过内部测试，基于LobeChat搭建的<a href="https://chat.mcjpg.org" target="_blank" rel="noreferrer">MCJPG Chat</a>正式发布<br> 搭建了AI工具的多模态应用链条，利用AI解决了在开发环境中各类问题</p><h3 id="优势" tabindex="-1">优势 <a class="header-anchor" href="#优势" aria-label="Permalink to &quot;优势&quot;">​</a></h3><p>采用稳定的上游官方API对接，能够确保我们的服务稳定提供<br> 采用成熟的框架搭建，用户的数据安全十分甚至九分安全（我们自己都看不见）<br> 免费提供了GPT-4o、Claude、Gemini 2.0 、DeepSeek R1等著名国内外商业大语言模型供用户使用，没有任何收费选项，完全由社区提供无条件捐助</p><h2 id="最后" tabindex="-1">最后 <a class="header-anchor" href="#最后" aria-label="Permalink to &quot;最后&quot;">​</a></h2><p>祝大家在新的一年里身体健康，事业进步，学业有成<br> 在接下来的时间，我们将继续努力，不断壮大组织规模，提升技术水平，加强宣传推广，优化审核机制，为广大玩家和服主提供更加优质的服务。</p>',9))])}const C=r(h,[["render",c]]);export{u as __pageData,C as default};
